    function indexToDiverseHexColor(index: number): string {
        // Use bitwise operations to generate more diverse colors
        let r = (index * 11909) & 255;
        let g = (index * 52973) & 255;
        let b = (index * 87311) & 255;
        // Convert each color component to a hex string and concatenate them
        return '#' + [r, g, b].map(c => c.toString(16).padStart(2, '0')).join('').toUpperCase();
    }

	function getColor(level: number) {
		switch (level) {
			case 0:
				return '#0e0e0e'
			case 1:
				return '#00442a'
			case 2:
				return '#006d35'
			case 3:
				return '#00a648'
			case 4:
				return '#00d35c'
            default:
                return '#0e0e0e'
		}
	}

	function normalize(count: number, base = 4, offset = 2) {
		switch (true) {
			case count === 0:
				return base
			case count > 40:
				return count
			default:
				return count * (base * offset)
		}
	}

